import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Profile from './Profile';

function App() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    favoriteSeason: 'Spring',
  });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validations
    const firstNameRegex = /^[A-Za-z]+$/;
    const lastNameRegex = /^[A-Za-z]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    let errors = {};

    if (!firstNameRegex.test(formData.firstName)) {
      errors.firstName = 'First Name must contain only alphabets.';
    }

    if (!lastNameRegex.test(formData.lastName)) {
      errors.lastName = 'Last Name must contain only alphabets.';
    }

    if (!emailRegex.test(formData.email)) {
      errors.email = 'Invalid email format.';
    }

    if (!passwordRegex.test(formData.password)) {
      errors.password =
        'Password must contain at least 8 characters, including at least one uppercase letter, one lowercase letter, one number and one special character.';
    }

    if (Object.keys(errors).length === 0) {
      // All fields are valid, render profile page
      setSubmitted(true);
    } else {
      // Handle errors, you can display them to the user or perform any other action
      console.log('Form has errors:', errors);
    }
  };


  return (
    <Router>
      <div className="App">
        <h1>Form</h1>
        <Switch>
          <Route path="/" exact>
            <form onSubmit={handleSubmit}>
              {/* Form inputs */}
            </form>
          </Route>
          <Route path="/profile">
            <Profile formData={formData} />
          </Route>
        </Switch>
        {submitted && <Profile formData={formData} />}
      </div>
    </Router>
  );
}

export default App;
