// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Dashboard from './Dashboard';

function App() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    favoriteSeason: 'Spring',
  });
  const [submitted, setSubmitted] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validations
    const firstNameRegex = /^[A-Za-z]+$/;
    const lastNameRegex = /^[A-Za-z]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    let newErrors = {};

    if (!firstNameRegex.test(formData.firstName)) {
      newErrors.firstName = 'First Name must contain only alphabets.';
    }

    if (!lastNameRegex.test(formData.lastName)) {
      newErrors.lastName = 'Last Name must contain only alphabets.';
    }

    if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Invalid email format.';
    }

    if (!passwordRegex.test(formData.password)) {
      newErrors.password =
        'Password must contain at least 8 characters, including at least one uppercase letter, one lowercase letter, one number and one special character.';
    }

    if (Object.keys(newErrors).length === 0) {
      // All fields are valid, render profile page
      setSubmitted(true);
      setErrors({});
    } else {
      // Handle errors, display them below the form
      setErrors(newErrors);
    }
  };

  const renderProfilePage = () => {
    return (
      <div>
        <h2>Profile</h2>
        <p><strong>First Name:</strong> {formData.firstName}</p>
        <p><strong>Last Name:</strong> {formData.lastName}</p>
        <p><strong>Email:</strong> {formData.email}</p>
        <p><strong>Favorite Season:</strong> {formData.favoriteSeason}</p>
        <Link to="/dashboard">Go to Dashboard</Link>
      </div>
    );
  };

  return (
    <div className="App">
      <Router>
        <h1>Form</h1>
        {!submitted ? (
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="firstName">First Name:</label>
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                pattern="[A-Za-z]+"
                required
              />
              {errors.firstName && <div className="error">{errors.firstName}</div>}
            </div>
            {/* Remaining form fields and validation errors */}
            <button type="submit">Submit</button>
          </form>
        ) : (
          renderProfilePage()
        )}
        <Routes>
          <Route path="/dashboard" element={<Dashboard />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
