import React, { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    favoriteSeason: 'Spring',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validations
    const firstNameRegex = /^[A-Za-z]+$/;
    const lastNameRegex = /^[A-Za-z]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    let errors = {};

    if (!firstNameRegex.test(formData.firstName)) {
      errors.firstName = 'First Name must contain only alphabets.';
    }

    if (!lastNameRegex.test(formData.lastName)) {
      errors.lastName = 'Last Name must contain only alphabets.';
    }

    if (!emailRegex.test(formData.email)) {
      errors.email = 'Invalid email format.';
    }

    if (!passwordRegex.test(formData.password)) {
      errors.password =
        'Password must contain at least 8 characters, including at least one uppercase letter, one lowercase letter, one number and one special character.';
    }

    if (Object.keys(errors).length === 0) {
      // All fields are valid, render profile page
      renderProfilePage();
    } else {
      // Handle errors, you can display them to the user or perform any other action
      console.log('Form has errors:', errors);
    }
  };

  const renderProfilePage = () => {
    // Render profile page
    // For simplicity, I'm just logging the profile data
    console.log('Profile Data:', {
      firstName: formData.firstName,
      lastName: formData.lastName,
      email: formData.email,
      favoriteSeason: formData.favoriteSeason,
    });
  };

  return (
    <div className="App">
      <h1>Form</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            pattern="[A-Za-z]+"
            required
          />
        </div>
        <div>
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            pattern="[A-Za-z]+"
            required
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$"
            title="Password must contain at least 8 characters, including at least one uppercase letter, one lowercase letter, one number and one special character."
            required
          />
        </div>
        <div>
          <label htmlFor="favoriteSeason">Favorite Season:</label>
          <select
            id="favoriteSeason"
            name="favoriteSeason"
            value={formData.favoriteSeason}
            onChange={handleChange}
            required
          >
            <option value="Spring">Spring</option>
            <option value="Fall">Fall</option>
            <option value="Winter">Winter</option>
          </select>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
