// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Dashboard from './Dashboard';

function App() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    favoriteSeason: 'Spring',
  });
  const [submitted, setSubmitted] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validations
    const firstNameRegex = /^[A-Za-z]+$/;
    const lastNameRegex = /^[A-Za-z]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    let newErrors = {};

    if (!firstNameRegex.test(formData.firstName)) {
      newErrors.firstName = 'First Name must contain only alphabets.';
    }

    if (!lastNameRegex.test(formData.lastName)) {
      newErrors.lastName = 'Last Name must contain only alphabets.';
    }

    if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Invalid email format.';
    }

    if (!passwordRegex.test(formData.password)) {
      newErrors.password =
        'Password must contain at least 8 characters, including at least one uppercase letter, one lowercase letter, one number and one special character.';
    }

    if (Object.keys(newErrors).length === 0) {
      // All fields are valid, render profile page
      setSubmitted(true);
      setErrors({});
    } else {
      // Handle errors, display them below the form
      setErrors(newErrors);
    }
  };

  const renderProfilePage = () => {
    return (
      <div>
        <h2>Profile</h2>
        <p><strong>First Name:</strong> {formData.firstName}</p>
        <p><strong>Last Name:</strong> {formData.lastName}</p>
        <p><strong>Email:</strong> {formData.email}</p>
        <p><strong>Favorite Season:</strong> {formData.favoriteSeason}</p>
        <Link to="/dashboard">Go to Dashboard</Link>
      </div>
    );
  };

  return (
    <Router>
      <div className="App">
        {!submitted ? (
          <>
            <h1>Form</h1>
            <form onSubmit={handleSubmit}>
              <div>
                <label htmlFor="firstName">First Name:</label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  pattern="[A-Za-z]+"
                  required
                />
                {errors.firstName && <div className="error">{errors.firstName}</div>}
              </div>
              <div>
                <label htmlFor="lastName">Last Name:</label>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  pattern="[A-Za-z]+"
                  required
                />
                {errors.lastName && <div className="error">{errors.lastName}</div>}
              </div>
              <div>
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
                {errors.email && <div className="error">{errors.email}</div>}
              </div>
              <div>
                <label htmlFor="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$"
                  title="Password must contain at least 8 characters, including at least one uppercase letter, one lowercase letter, one number and one special character."
                  required
                />
                {errors.password && <div className="error">{errors.password}</div>}
              </div>
              <div>
                <label htmlFor="favoriteSeason">Favorite Season:</label>
                <select
                  id="favoriteSeason"
                  name="favoriteSeason"
                  value={formData.favoriteSeason}
                  onChange={handleChange}
                  required
                >
                  <option value="Spring">Spring</option>
                  <option value="Fall">Fall</option>
                  <option value="Winter">Winter</option>
                </select>
              </div>
              <button type="submit">Submit</button>
            </form>
          </>
        ) : (
          renderProfilePage()
        )}
      </div>
      <Route path="/dashboard" component={Dashboard} />
    </Router>
  );
}

export default App;
